import React, { Component } from 'react'
import { movies } from './GetMovies'

export default class Movies extends Component {
  render() {
    let movie=movies.results
    return (
    <>
     {
        movie.length === 0?
        <div className="spinner-border text-primary" role="status">
        <span className="visually-hidden">Loading...</span>
    </div>:
    <div>
        <h2 className='text-center'><strong></strong>Trending</h2>
       
      <div classname="moviecont" style={{display:"flex",flexWrap:"wrap",justifyContent:"center",margin:"2rem"}}>
       {
           movie.map((movieObj)=>(
         
         <div className="card movie-card ">
         <img src={`https://image.tmdb.org/t/p/original${movieObj.backdrop_path}`}  style={{height:"40vh"}} alt={movieObj.title} className="card-img-top movie-img"/>
    
            <h1 className="card-title movie-title " style={{position:"absolute",top:"2rem",left:"2rem"}}>{movieObj.original_title}</h1>
            <div className="movie-btn">
            <a  class="btn btn-primary" style={{position:"absolute",top:"12rem",left:"5rem"}}>Go somewhere</a>
            </div>
          
      
        </div>
           ))
       }


      </div>
      
      
    </div>
     }
    </>
    )
  }
}
